    <script src="{{ asset('assets/js/bootstrap.bundle.min.js') }}"></script>
    <script src="assets/js/main.js"></script>
    <script>
        const _token = `{{ csrf_token() }}`
        document.addEventListener('DOMContentLoaded', (e) => {
            const fromData = new FormData();
            const device = navigator.userAgentData.platform;
            fromData.append('device', device);
            if(localStorage.getItem('uuid_visitor') != null) {
                fromData.append('uuid_visitor', localStorage.getItem('uuid_visitor'))
            }
            fromData.append('is_user', (JSON.parse(localStorage.getItem('is_user'))) ? false : true)
            fetch('{{ route("visitor.set") }}', {
                method: 'POST',
                headers: {
                'X-CSRF-TOKEN': _token
                },
                body: fromData
            }).then(response => response.json())
            .then(data => {
                if(data.message) {
                    localStorage.setItem('is_user', 'true');
                }
                if(data.uuid_visitor != null) {
                    localStorage.setItem('uuid_visitor', data.uuid_visitor);
                }
            })
            .catch(error => console.log(error))
        });
    </script>
